import axios from "axios";
import { useState, useEffect } from 'react'

function App() {
  const [search, setSearch] = useState('')
  const [isSearch, setIsSearch] = useState(false)
  const [countries, setCountries] = useState([])

  const hook = () => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        setCountries(response.data)
      })
  }

  useEffect(hook, [])

  const userInput = (event) => {
    setSearch(event.target.value)
    setIsSearch(event.target.value.length > 0);
  }

  const searchResult = isSearch ? 
        countries.filter(country => 
          country.name.common === search) : countries

  return (
    <div>
      <Query search={search} onChange={userInput}/>
      <Display data={searchResult} search/>
    </div>
  )
}

const Display = ({ data }) => {
  return (
    <div>
      {data.name.common}
    </div>
  )
}

const Query = ({ search, onChange }) => {
  return (
  <div>
    find countries <input value={search} onChange={onChange}/>
  </div>
  )
}

export default App;
